import React from 'react';
import './index.css';
import { useState, useEffect } from 'react';
import { Gauge } from '@ant-design/charts';
import { Pie } from '@ant-design/charts';

const DemoGauge1: React.FC = () => {
    var config = {
      percent: 0.75,
      range: { color: 'l(0) 0:#bde8ff 1:#9ec9ff' },
      startAngle: Math.PI,
      endAngle: 2 * Math.PI,
      indicator: null,
      statistic: {
        title: {
          offsetY: -10,
          style: {
            fontSize: '36px',
            color: '#4B535E',
          },
          formatter: function formatter() {
            return '70%';
          },
        },
      },
    };
    return <Gauge {...config} />;
};
const DemoGauge2: React.FC = () => {
    var config = {
      percent: 0.75,
      range: { color: 'l(0) 0:#bde8ff 1:#9ec9ff' },
      startAngle: Math.PI,
      endAngle: 2 * Math.PI,
      indicator: null,
      statistic: {
        title: {
          offsetY: -10,
          style: {
            fontSize: '36px',
            color: '#4B535E',
          },
          formatter: function formatter() {
            return '70%';
          },
        },
      },
    };
    return <Gauge {...config} />;
};  
const DemoPie: React.FC = () => {
    var data = [
      {
        type: '分类一',
        value: 27,
      },
      {
        type: '分类二',
        value: 25,
      },
      {
        type: '分类三',
        value: 18,
      },
      {
        type: '分类四',
        value: 15,
      },
      {
        type: '分类五',
        value: 10,
      },
      {
        type: '其他',
        value: 5,
      },
    ];
    var config = {
      appendPadding: 10,
      data: data,
      angleField: 'value',
      colorField: 'type',
      radius: 0.8,
      label: {
        type: 'spider',
        labelHeight: 28,
        content: '{name}\n{percentage}',
      },
      interactions: [{ type: 'element-selected' }, { type: 'element-active' }],
    };
    return <Pie {...config} />;
  };

class MyPie extends React.Component {
    render() {
        return (
        <div id="pie">
            <div>
                <div>設備稼動率</div>
                <div className="guage"><DemoGauge1 /></div>
            </div>
            <div>
                <div>當前機台狀態分析</div>
                <div><DemoPie /></div>
            </div>
            <div>
                <div>設備關機比率</div>
                <div className="guage"><DemoGauge2 /></div>
            </div>
        </div>
        );
    }
}
export default MyPie
